import React, { useState, useEffect } from 'react';
import './Slide.css';
import { CSSTransition } from "react-transition-group";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import topimg1 from '../image/top1.jpg';
import topimg2 from '../image/top2.jpg';
import topimg3 from '../image/top3.jpg';
import topimg4 from '../image/top4.jpg';
import topimg5 from '../image/top5.jpg';
import topimg6 from '../image/top6.jpg';
// import FadeIn from 'react-fade-in';
// const [count, setCount] = useState(0);

class Slide extends React.Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      fade: true, 
      arrows: false,
      // centerMode: true,
    };
    return (
         <Slider {...settings} className="slide">
           <div className="header-img header-img0"> </div>
           <div className="header-img header-img1"> </div>
           <div className="header-img header-img2"> </div>
           <div className="header-img header-img3"> </div>
           <div className="header-img header-img4"> </div>
           <div className="header-img header-img5"> </div>
         </Slider>
      );
  }
}

