import React from 'react';
import './Header.css';
import Top from './Top/Top.js';
import Top2 from './Top/Top2.js';
import SwipeableViews from 'react-swipeable-views';
import {autoPlay} from 'react-swipeable-views-utils';
import top_img from './image/top.jpg';
import top1 from './image/top1.jpg';
import top2 from './image/top2.jpg';
import top3 from './image/top3.jpg';
import top4 from './image/top4.jpg';
import top5 from './image/top5.jpg';
import top6 from './image/top6.jpg';

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {currentPosition: 0};
  }

  componentDidMount() {
    window.addEventListener('scroll', event => this.watchCurrentPosition(), true)
  }

  componentWillUnmount() {
    window.removeEventListener('scroll')
  }

  watchCurrentPosition() {
    this.setState({currentPosition: this.scrollTop()});
  }

  scrollTop() {
    return Math.max(
      window.pageYOffset,
      document.documentElement.scrollTop,
      document.body.scrollTop
    );
  }

  getWindowSize() {
    var w = window,
    d = document,
    e = d.documentElement,
    g = d.getElementsByTagName('body')[0],
    width = w.innerWidth || e.clientWidth || g.clientWidth,
    height = w.innerHeight|| e.clientHeight|| g.clientHeight;
    return {
      width: width,
      height: height
    }
  }

  // componentWillMount () {
  //   window.addEventListener('resize', () => {
  //     var size = this.getWindowSize()
  //   })
  // }

  render() {
    let topTex;
    if(this.state.currentPosition > this.getWindowSize().height * 0.8){
      topTex=(
        <Top />
      );
    }

    let topTex2;
    if(this.state.currentPosition <= this.getWindowSize().height * 0.8){
      topTex2=(
        <Top2 />
      );
    }
    const TopImgList = [{name: top1}, {name: top2}, {name: top3}, {name: top4}, {name: top5}, {name: top6}];

    const EnhancedSwipeableViews = autoPlay(SwipeableViews);

    // const index=[1, 2, 3, 4, 5, 6];
    // imgTex=({index}) =>(
    //       <div className="header-img" style={{backgroundImage: `url(${top1})`}}></div>
    //   );
    // const startTime = Date.now();
    // let nowTime = Date.now();
    let imgTex;
    if (0 <= (Date.now()) % 12 && (Date.now()) % 12 < 3){
      imgTex=(
        <div className="header-img" style={{backgroundImage: `url(${top1})`}}/>
        )
    }
    if (3 <= (Date.now()) % 12 && (Date.now())% 12 < 6){
      imgTex=(
        <div className="header-img" style={{backgroundImage: `url(${top2})`}}/>
        )
    }
    if (6 <= (Date.now()) % 12 && (Date.now()) % 12 < 9){
      imgTex=(
        <div className="header-img" style={{backgroundImage: `url(${top3})`}}/>
        )
    }
    if (9 <= (Date.now()) % 12 && (Date.now()) % 12 < 12){
      imgTex=(
        <div className="header-img" style={{backgroundImage: `url(${top4})`}}/>
        )
    }

    return (
     <div className="header">
      {/*<EnhancedSwipeableViews enableMouseEvents interval={2500}>*/}
      {/*style={{backgroundImage: `url(${top1})`}}*/}
      {/*<EnhancedSwipeableViews enableMouseEvents interval={2500}>
      </EnhancedSwipeableViews>*/}
      <header className="App-header">
            {/*<EnhancedSwipeableViews slideRenderer={imgTex}/>*/}
            {imgTex}
            {/*<div className="header-img imgh1" style={{backgroundImage: `url(${top1})`}}></div>
            <div className="header-img imgh2" style={{backgroundImage: `url(${top2})`}}></div>
            <div className="header-img imgh3" style={{backgroundImage: `url(${top3})`}}></div>
            <div className="header-img imgh4" style={{backgroundImage: `url(${top4})`}}></div>*/}
            {/*</EnhancedSwipeableViews>*/}
            {topTex}
            {topTex2}
            <div className="header-text ht1">
              Hello!<br />I'm Yuga!
            </div>
            <div className="header-text ht2">
              Osaka University stutent
            </div>
            <div className="header-text ht3">
              scroll
            </div>
            <span className="header-guid hg1"></span>
            <span className="header-guid hg2"></span>
            <span className="header-guid hg3"></span>
      </header>
      {/*<header className="App-header" style={{backgroundImage: `url(${top2})`}} >
            {topTex}
            {topTex2}
            <div className="header-text ht1">
              Hello!<br />I'm Yuga!
            </div>
            <div className="header-text ht2">
              Osaka University stutent
            </div>
            <div className="header-text ht3">
              scroll
            </div>
            <span className="header-guid hg1"></span>
            <span className="header-guid hg2"></span>
            <span className="header-guid hg3"></span>
      </header>*/}
      {/*{TopImgList.map((imgItem) => {
        return (
          <header className="App-header" style={{backgroundImage: `url(${imgItem})`}} >
            {topTex}
            {topTex2}
            <div className="header-text ht1">
              Hello!<br />I'm Yuga!
            </div>
            <div className="header-text ht2">
              Osaka University stutent
            </div>
            <div className="header-text ht3">
              scroll
            </div>
            <span className="header-guid hg1"></span>
            <span className="header-guid hg2"></span>
            <span className="header-guid hg3"></span>
          </header>
        );
      })}*/}
      {/*</EnhancedSwipeableViews>*/}
    </div>
    );
  }
}

export default Header;
